import React, { useState } from 'react';
import { addStudent } from '../api';  // Import the API call

const AddStudent = ({ onAdd }) => {
  const [studentName, setStudentName] = useState('');

  const handleAddStudent = async () => {
    if (studentName) {
      const newStudent = await addStudent(studentName);  // Add the new student via API
      onAdd(newStudent);  // Notify parent component to update the student list
      setStudentName('');  // Reset input field
    }
  };

  return (
    <div>
      <input 
        type="text" 
        value={studentName} 
        onChange={(e) => setStudentName(e.target.value)} 
        placeholder="Student Name" 
      />
      <button onClick={handleAddStudent}>Add Student</button>
    </div>
  );
};

export default AddStudent;
