import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AddStudent from './components/AddStudent';
import StudentList from './components/StudentList';
import AddCourse from './components/AddCourse';
import CourseList from './components/CourseList';

function App() {
  // State to store students and courses
  const [students, setStudents] = useState([]);
  const [courses, setCourses] = useState([]);

  // Fetch students data from backend
  useEffect(() => {
    // Fetch students data
    axios.get('http://localhost:3000/students')
      .then(response => {
        console.log("Students fetched:", response.data);  // Debugging log
        setStudents(response.data);  // Update the students state
      })
      .catch(error => {
        console.error("There was an error fetching students!", error);
      });

    // Fetch courses data
    axios.get('http://localhost:3000/courses')
      .then(response => {
        console.log("Courses fetched:", response.data);  // Debugging log
        setCourses(response.data);  // Update the courses state
      })
      .catch(error => {
        console.error("There was an error fetching courses!", error);
      });
  }, []); // Empty dependency array means it runs only once when component mounts

  return (
    <div className="App">
      <h1>Student-Course Management</h1>

      <AddStudent setStudents={setStudents} />
      <StudentList students={students} />

      <AddCourse setCourses={setCourses} />
      <CourseList courses={courses} />
    </div>
  );
}

export default App;
