import React from 'react';

const CourseList = ({ courses }) => {
    return (
        <div>
            <ul>
                {courses.length === 0 ? (
                    <li>No courses available</li>
                ) : (
                    courses.map((course) => (
                        <li key={course.id}>
                            {course.name} ({course.department}) - {course.isOpen ? 'Open' : 'Closed'}
                        </li>
                    ))
                )}
            </ul>
        </div>
    );
};

export default CourseList;
