import React, { useState, useEffect } from "react";
import { getStudents, deleteStudent } from "../api";

const StudentList = () => {
  const [students, setStudents] = useState([]);

  useEffect(() => {
    const fetchStudents = async () => {
      const data = await getStudents();
      console.log("Students data: ", data); // Check the data
      setStudents(data);
    };
    fetchStudents();
  }, []);

  const handleDeleteStudent = async (id) => {
    await deleteStudent(id);
    setStudents(students.filter(student => student.id !== id)); // remove deleted student from list
  };

  return (
    <div>
      <h2>Students</h2>
      <button>Add Student</button>
      <ul>
        {students.length > 0 ? (
          students.map(student => (
            <li key={student.id}>
              <div>
                <strong>{student.name}</strong>
                <p>Department: {student.department || 'N/A'}</p> {/* Handle missing department */}
                <p>Semester: {student.semester || 'N/A'}</p> {/* Handle missing semester */}
                <p>Enrolled Courses: {student.enrolledCourses ? student.enrolledCourses.join(", ") : 'N/A'}</p> {/* Handle missing courses */}
                <button onClick={() => handleDeleteStudent(student.id)}>Delete</button>
              </div>
            </li>
          ))
        ) : (
          <p>No students available.</p>
        )}
      </ul>
    </div>
  );
};

export default StudentList;
