// src/App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AddStudentForm from './components/AddStudentForm';
import StudentList from './components/StudentList';
import DeleteStudent from './components/DeleteStudent';

function App() {
    const [students, setStudents] = useState([]);

    // Fetch the students from the backend when the app starts
    useEffect(() => {
        axios.get('http://localhost:3000/students')
            .then(response => setStudents(response.data))
            .catch(error => console.error('Error fetching student data:', error));
    }, []);

    // Handler for deleting a student (updating the UI)
    const handleDelete = (studentId) => {
        setStudents(students.filter(student => student.id !== studentId));
    };

    return (
        <div>
            <h1>Student-Course Application</h1>

            <AddStudentForm /> {/* Add New Student Form */}

            <h2>Student List</h2>
            <StudentList students={students} /> {/* Display List of Students */}

            {/* Loop through students and render delete button for each */}
            {students.map(student => (
                <DeleteStudent key={student.id} studentId={student.id} onDelete={handleDelete} />
            ))}
        </div>
    );
}

export default App;
