import axios from 'axios';

// Fetch all courses
export const getCourses = async () => {
  try {
    const response = await axios.get('http://localhost:5000/api/courses');
    return response.data;
  } catch (error) {
    console.error('Error fetching courses:', error);
    return [];
  }
};

// Fetch all students
export const getStudents = async () => {
  try {
    const response = await axios.get('http://localhost:5000/api/students');
    return response.data;
  } catch (error) {
    console.error('Error fetching students:', error);
    return [];
  }
};

// Add a new course
export const addCourse = async (name) => {
  try {
    const response = await axios.post('http://localhost:5000/api/courses', { name });
    return response.data;  // Return the newly added course
  } catch (error) {
    console.error('Error adding course:', error);
  }
};

// Add a new student
export const addStudent = async (name) => {
  try {
    const response = await axios.post('http://localhost:5000/api/students', { name });
    return response.data;  // Return the newly added student
  } catch (error) {
    console.error('Error adding student:', error);
  }
};

// Delete a course
export const deleteCourse = async (id) => {
  try {
    await axios.delete(`http://localhost:5000/api/courses/${id}`);
  } catch (error) {
    console.error('Error deleting course:', error);
  }
};

// Delete a student
export const deleteStudent = async (id) => {
  try {
    await axios.delete(`http://localhost:5000/api/students/${id}`);
  } catch (error) {
    console.error('Error deleting student:', error);
  }
};
