// src/components/CourseList.js

import React, { useState, useEffect } from 'react';
import { getCourses, deleteCourse } from '../api'; // Make sure the deleteCourse function is imported

const CourseList = () => {
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    // Fetch the courses from the backend when the component mounts
    getCourses()
      .then((response) => {
        console.log('Courses data:', response.data); // Log the data to verify it's coming in
        if (Array.isArray(response.data)) {
          setCourses(response.data); // Update the state with the fetched courses
        } else {
          console.error('Courses data is not an array:', response.data);
        }
      })
      .catch((error) => {
        console.error('Error fetching courses:', error);
      });
  }, []);

  // Handle the deletion of a course
  const handleDelete = (courseId) => {
    deleteCourse(courseId)
      .then(() => {
        setCourses(courses.filter(course => course.id !== courseId)); // Remove the deleted course from the state
      })
      .catch((error) => {
        console.error('Error deleting course:', error);
      });
  };

  return (
    <div>
      <h2>Courses</h2>
      {courses.length === 0 ? (
        <p>No courses available.</p>
      ) : (
        courses.map((course) => (
          <div key={course.id}>
            <p>{course.name}</p>
            <button onClick={() => handleDelete(course.id)}>Delete</button>
          </div>
        ))
      )}
    </div>
  );
};

export default CourseList;
