import React, { useState } from 'react';
import axios from 'axios';

const AddCourse = ({ setCourses }) => {
    const [course, setCourse] = useState({
        name: '',
        department: '',
        isOpen: false
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setCourse((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        // Prepare course data
        const newCourse = {
            name: course.name,
            department: course.department,
            isOpen: course.isOpen === 'true',
        };

        // Send a POST request to add the course
        axios.post('http://localhost:3000/courses', newCourse)
            .then((response) => {
                setCourses((prev) => [...prev, response.data]); // Update the courses list
            })
            .catch((error) => {
                console.error('Error adding course:', error);
            });
    };

    return (
        <div>
            <h2>Add New Course</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="name"
                    placeholder="Course Name"
                    value={course.name}
                    onChange={handleChange}
                    required
                />
                <input
                    type="text"
                    name="department"
                    placeholder="Department"
                    value={course.department}
                    onChange={handleChange}
                    required
                />
                <input
                    type="text"
                    name="isOpen"
                    placeholder="Is Course Open? (true/false)"
                    value={course.isOpen}
                    onChange={handleChange}
                    required
                />
                <button type="submit">Add Course</button>
            </form>
        </div>
    );
};

export default AddCourse;
