import React, { useEffect, useState } from 'react';
import { getCourses } from '../api';  // Import the API function to fetch courses

const CourseList = () => {
  const [courses, setCourses] = useState([]);  // State for holding courses

  // Fetch courses when the component mounts
  useEffect(() => {
    const fetchCourses = async () => {
      const data = await getCourses();  // Get data from API
      setCourses(data);  // Update state with courses data
    };
    fetchCourses();
  }, []);  // Empty dependency array means this effect runs once when the component mounts

  return (
    <div>
      <h3>Courses</h3>
      {courses.length === 0 ? (
        <p>No courses available.</p>
      ) : (
        <ul>
          {courses.map((course) => (
            <li key={course.id}>{course.name}</li>  // Display each course name
          ))}
        </ul>
      )}
    </div>
  );
};

export default CourseList;
