// src/components/DeleteStudent.js
import React from 'react';
import axios from 'axios';

const DeleteStudent = ({ studentId, onDelete }) => {
    const handleDelete = () => {
        // Send DELETE request to backend to delete the student
        axios.delete(`http://localhost:3000/students/${studentId}`)
            .then(response => {
                console.log('Student deleted:', response);
                onDelete(studentId); // Update UI by removing the student
            })
            .catch(error => {
                console.error('Error deleting student:', error);
            });
    };

    return (
        <button onClick={handleDelete}>Delete</button>
    );
};

export default DeleteStudent;
