import React, { useState } from 'react';
import axios from 'axios';

const AddStudent = ({ setStudents }) => {
    const [student, setStudent] = useState({
        name: '',
        department: '',
        semester: '',
        enrolledCourses: '',
        completedCourses: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setStudent((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        // Prepare student data
        const newStudent = {
            name: student.name,
            department: student.department,
            semester: student.semester,
            enrolledCourses: student.enrolledCourses.split(','),
            completedCourses: student.completedCourses.split(','),
        };

        // Send a POST request to add the student
        axios.post('http://localhost:3000/students', newStudent)
            .then((response) => {
                setStudents((prev) => [...prev, response.data]); // Update the students list
            })
            .catch((error) => {
                console.error('Error adding student:', error);
            });
    };

    return (
        <div>
            <h2>Add New Student</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="name"
                    placeholder="Name"
                    value={student.name}
                    onChange={handleChange}
                    required
                />
                <input
                    type="text"
                    name="department"
                    placeholder="Department"
                    value={student.department}
                    onChange={handleChange}
                    required
                />
                <input
                    type="number"
                    name="semester"
                    placeholder="Semester"
                    value={student.semester}
                    onChange={handleChange}
                    required
                />
                <input
                    type="text"
                    name="enrolledCourses"
                    placeholder="Enrolled Courses (comma separated)"
                    value={student.enrolledCourses}
                    onChange={handleChange}
                    required
                />
                <input
                    type="text"
                    name="completedCourses"
                    placeholder="Completed Courses (comma separated)"
                    value={student.completedCourses}
                    onChange={handleChange}
                    required
                />
                <button type="submit">Add Student</button>
            </form>
        </div>
    );
};

export default AddStudent;
