import React, { useState } from 'react';
import { addCourse } from '../api';  // Import the API call

const AddCourse = ({ onAdd }) => {
  const [courseName, setCourseName] = useState('');

  const handleAddCourse = async () => {
    if (courseName) {
      const newCourse = await addCourse(courseName);  // Add the new course via API
      onAdd(newCourse);  // Notify parent component to update the course list
      setCourseName('');  // Reset input field
    }
  };

  return (
    <div>
      <input 
        type="text" 
        value={courseName} 
        onChange={(e) => setCourseName(e.target.value)} 
        placeholder="Course Name" 
      />
      <button onClick={handleAddCourse}>Add Course</button>
    </div>
  );
};

export default AddCourse;
