import React, { useEffect, useState } from 'react';
import { getStudents } from '../api';  // Import the API function to fetch students

const StudentList = () => {
  const [students, setStudents] = useState([]);  // State for holding students

  // Fetch students when the component mounts
  useEffect(() => {
    const fetchStudents = async () => {
      const data = await getStudents();  // Get data from API
      setStudents(data);  // Update state with students data
    };
    fetchStudents();
  }, []);  // Empty dependency array means this effect runs once when the component mounts

  return (
    <div>
      <h3>Students</h3>
      {students.length === 0 ? (
        <p>No students available.</p>
      ) : (
        <ul>
          {students.map((student) => (
            <li key={student.id}>{student.name}</li>  // Display each student name
          ))}
        </ul>
      )}
    </div>
  );
};

export default StudentList;
