// src/components/AddStudentForm.js
import React, { useState } from 'react';
import axios from 'axios';

const AddStudentForm = () => {
    const [name, setName] = useState('');
    const [department, setDepartment] = useState('');
    const [semester, setSemester] = useState('');
    const [enrolledCourses, setEnrolledCourses] = useState('');
    const [completedCourses, setCompletedCourses] = useState('');

    const handleSubmit = (event) => {
        event.preventDefault();

        const newStudent = {
            name,
            department,
            semester,
            enrolledCourses: enrolledCourses.split(','),
            completedCourses: completedCourses.split(','),
        };

        // Make POST request to the backend to add a new student
        axios.post('http://localhost:3000/students', newStudent)
            .then(response => {
                console.log('Student added:', response.data);
            })
            .catch(error => {
                console.error('Error adding student:', error);
            });

        // Reset form fields after submission
        setName('');
        setDepartment('');
        setSemester('');
        setEnrolledCourses('');
        setCompletedCourses('');
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Add a New Student</h2>
            <input
                type="text"
                placeholder="Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
            />
            <input
                type="text"
                placeholder="Department"
                value={department}
                onChange={(e) => setDepartment(e.target.value)}
                required
            />
            <input
                type="number"
                placeholder="Semester"
                value={semester}
                onChange={(e) => setSemester(e.target.value)}
                required
            />
            <input
                type="text"
                placeholder="Enrolled Courses (comma separated)"
                value={enrolledCourses}
                onChange={(e) => setEnrolledCourses(e.target.value)}
                required
            />
            <input
                type="text"
                placeholder="Completed Courses (comma separated)"
                value={completedCourses}
                onChange={(e) => setCompletedCourses(e.target.value)}
            />
            <button type="submit">Add Student</button>
        </form>
    );
};

export default AddStudentForm;
