import axios from 'axios';

const API_URL = 'http://localhost:5000/api';  // Adjust the port if necessary

// Existing functions
export const getCourses = () => axios.get(`${API_URL}/courses`);
export const getStudents = () => axios.get(`${API_URL}/students`);
export const addCourse = (course) => axios.post(`${API_URL}/courses`, course);
export const addStudent = (student) => axios.post(`${API_URL}/students`, student);
export const enrollStudentInCourse = (studentId, courseId) =>
  axios.post(`${API_URL}/students/${studentId}/courses/${courseId}`);

// New functions to delete courses and students
export const deleteCourse = (courseId) => {
  return axios.delete(`${API_URL}/courses/${courseId}`);
};

export const deleteStudent = (studentId) => {
  return axios.delete(`${API_URL}/students/${studentId}`);
};
