import React, { useState, useEffect } from "react";
import { getStudents, deleteStudent } from "../api";

const StudentList = () => {
  const [students, setStudents] = useState([]);
  const [error, setError] = useState(null);  // Added state for errors

  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const data = await getStudents();
        setStudents(data);
      } catch (err) {
        setError("Failed to fetch students.");
        console.error("Error fetching students:", err);
      }
    };
    fetchStudents();
  }, []);

  const handleDeleteStudent = async (id) => {
    await deleteStudent(id);
    setStudents(students.filter(student => student.id !== id));
  };

  return (
    <div>
      <h2>Students</h2>
      {error && <p>{error}</p>} {/* Display error message if any */}
      <button>Add Student</button>
      <ul>
        {students.length > 0 ? (
          students.map(student => (
            <li key={student.id}>
              <div>
                <strong>{student.name}</strong>
                <p>Department: {student.department || 'N/A'}</p>
                <p>Semester: {student.semester || 'N/A'}</p>
                <p>Enrolled Courses: {student.enrolledCourses ? student.enrolledCourses.join(", ") : 'N/A'}</p>
                <button onClick={() => handleDeleteStudent(student.id)}>Delete</button>
              </div>
            </li>
          ))
        ) : (
          <p>No students available.</p>
        )}
      </ul>
    </div>
  );
};

export default StudentList;
