import axios from 'axios';

const API_URL = 'http://localhost:5000/api';  // Backend running on port 5000

// Fetch all students
export const getStudents = () => {
  return axios.get(`${API_URL}/students`);
};

// Add a new student
export const addStudent = (studentData) => {
  return axios.post(`${API_URL}/students`, studentData);
};

// Fetch all courses
export const getCourses = () => {
  return axios.get(`${API_URL}/courses`);
};

// Add a new course
export const addCourse = (courseData) => {
  return axios.post(`${API_URL}/courses`, courseData);
};

// Enroll a student in a course
export const enrollStudentInCourse = (studentId, courseName) => {
  return axios.post(`${API_URL}/students/enroll`, { studentId, courseName });
};
