import React from 'react';

const CourseList = ({ courses }) => {
  return (
    <div>
      <h2>Course List</h2>
      {courses.length === 0 ? (
        <p>No courses available</p>
      ) : (
        <ul>
          {courses.map(course => (
            <li key={course.id}>
              {course.name} ({course.department}) - {course.isOpen ? 'Open' : 'Closed'}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default CourseList;
