import React, { useState, useEffect } from 'react';
import { getCourses, deleteCourse, addCourse } from '../api';
import AddCourse from './AddCourse';  // Make sure to import your AddCourse component

const CourseList = () => {
  const [courses, setCourses] = useState([]);

  // Fetch courses when the component mounts
  useEffect(() => {
    const fetchCourses = async () => {
      const courseData = await getCourses();
      setCourses(courseData);
    };
    fetchCourses();
  }, []);

  // Handle deleting a course
  const handleDeleteCourse = async (id) => {
    await deleteCourse(id);
    setCourses(courses.filter(course => course.id !== id));  // Remove the course from state
  };

  // Handle adding a new course
  const handleAddCourse = (newCourse) => {
    setCourses([...courses, newCourse]);  // Add the new course to the state
  };

  return (
    <div>
      <h3>Courses</h3>
      <AddCourse onAdd={handleAddCourse} /> {/* Add course form */}
      {courses.length === 0 ? (
        <p>No courses available.</p>
      ) : (
        <ul>
          {courses.map((course) => (
            <li key={course.id}>
              {course.name}
              <button onClick={() => handleDeleteCourse(course.id)}>Delete</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default CourseList;
