import React, { useState } from 'react';
import axios from 'axios';

const AddStudent = ({ setStudents }) => {
  const [name, setName] = useState('');
  const [department, setDepartment] = useState('');
  const [semester, setSemester] = useState('');
  const [enrolledCourses, setEnrolledCourses] = useState('');
  const [completedCourses, setCompletedCourses] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    const newStudent = {
      name,
      department,
      semester: parseInt(semester),
      enrolledCourses: enrolledCourses.split(',').map(course => course.trim()),
      completedCourses: completedCourses.split(',').map(course => course.trim())
    };

    axios.post('http://localhost:3000/students', newStudent)
      .then(response => {
        setStudents(prevStudents => [...prevStudents, response.data]);
        // Reset form after submission
        setName('');
        setDepartment('');
        setSemester('');
        setEnrolledCourses('');
        setCompletedCourses('');
      })
      .catch(error => {
        console.error("There was an error adding the student!", error);
      });
  };

  return (
    <div>
      <h2>Add New Student</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        </div>
        <div>
          <label>Department:</label>
          <input type="text" value={department} onChange={(e) => setDepartment(e.target.value)} />
        </div>
        <div>
          <label>Semester:</label>
          <input type="number" value={semester} onChange={(e) => setSemester(e.target.value)} />
        </div>
        <div>
          <label>Enrolled Courses (comma separated):</label>
          <input type="text" value={enrolledCourses} onChange={(e) => setEnrolledCourses(e.target.value)} />
        </div>
        <div>
          <label>Completed Courses (comma separated):</label>
          <input type="text" value={completedCourses} onChange={(e) => setCompletedCourses(e.target.value)} />
        </div>
        <button type="submit">Add Student</button>
      </form>
    </div>
  );
};

export default AddStudent;
