import React, { useState, useEffect } from 'react';
import { getStudents, deleteStudent, addStudent } from '../api';
import AddStudent from './AddStudent';  // Make sure to import your AddStudent component

const StudentList = () => {
  const [students, setStudents] = useState([]);

  // Fetch students when the component mounts
  useEffect(() => {
    const fetchStudents = async () => {
      const studentData = await getStudents();
      setStudents(studentData);
    };
    fetchStudents();
  }, []);

  // Handle deleting a student
  const handleDeleteStudent = async (id) => {
    await deleteStudent(id);
    setStudents(students.filter(student => student.id !== id));  // Remove the student from state
  };

  // Handle adding a new student
  const handleAddStudent = (newStudent) => {
    setStudents([...students, newStudent]);  // Add the new student to the state
  };

  return (
    <div>
      <h3>Students</h3>
      <AddStudent onAdd={handleAddStudent} /> {/* Add student form */}
      {students.length === 0 ? (
        <p>No students available.</p>
      ) : (
        <ul>
          {students.map((student) => (
            <li key={student.id}>
              {student.name}
              <button onClick={() => handleDeleteStudent(student.id)}>Delete</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default StudentList;
