// src/CourseList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CourseList = () => {
  const [courses, setCourses] = useState([]);
  const [newCourse, setNewCourse] = useState({
    name: '',
    department: '',
    isOpen: true,
  });

  useEffect(() => {
    // Fetch the course data from the backend
    axios.get('http://localhost:3000/courses')
      .then(response => {
        setCourses(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the course data!', error);
      });
  }, []);

  // Add a new course
  const handleAddCourse = () => {
    axios.post('http://localhost:3000/courses', newCourse)
      .then(response => {
        setCourses([...courses, response.data]);
        setNewCourse({ name: '', department: '', isOpen: true });
      })
      .catch(error => {
        console.error('There was an error adding the course!', error);
      });
  };

  // Handle form changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewCourse({
      ...newCourse,
      [name]: value,
    });
  };

  return (
    <div>
      <h2>Course List</h2>
      <ul>
        {courses.map(course => (
          <li key={course.id}>
            <h3>{course.name}</h3>
            <p>Department: {course.department}</p>
            <p>Status: {course.isOpen ? 'Open' : 'Closed'}</p>
          </li>
        ))}
      </ul>

      <h2>Add a New Course</h2>
      <form>
        <input
          type="text"
          name="name"
          placeholder="Course Name"
          value={newCourse.name}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="department"
          placeholder="Department"
          value={newCourse.department}
          onChange={handleInputChange}
        />
        <select
          name="isOpen"
          value={newCourse.isOpen}
          onChange={handleInputChange}
        >
          <option value={true}>Open</option>
          <option value={false}>Closed</option>
        </select>
        <button type="button" onClick={handleAddCourse}>Add Course</button>
      </form>
    </div>
  );
};

export default CourseList;
