// src/StudentList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const StudentList = () => {
  const [students, setStudents] = useState([]);
  const [newStudent, setNewStudent] = useState({
    name: '',
    department: '',
    semester: '',
    enrolledCourses: [],
    completedCourses: [],
  });

  useEffect(() => {
    // Fetch the student data from the backend
    axios.get('http://localhost:3000/students')
      .then(response => {
        setStudents(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the student data!', error);
      });
  }, []);

  // Add a new student
  const handleAddStudent = () => {
    axios.post('http://localhost:3000/students', newStudent)
      .then(response => {
        setStudents([...students, response.data]);
        setNewStudent({ name: '', department: '', semester: '', enrolledCourses: [], completedCourses: [] });
      })
      .catch(error => {
        console.error('There was an error adding the student!', error);
      });
  };

  // Handle form changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewStudent({
      ...newStudent,
      [name]: value,
    });
  };

  return (
    <div>
      <h2>Student List</h2>
      <ul>
        {students.map(student => (
          <li key={student.id}>
            <h3>{student.name}</h3>
            <p>Department: {student.department}</p>
            <p>Semester: {student.semester}</p>
            <p>Enrolled Courses: {student.enrolledCourses.join(', ')}</p>
            <p>Completed Courses: {student.completedCourses.join(', ')}</p>
          </li>
        ))}
      </ul>

      <h2>Add a New Student</h2>
      <form>
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={newStudent.name}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="department"
          placeholder="Department"
          value={newStudent.department}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="semester"
          placeholder="Semester"
          value={newStudent.semester}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="enrolledCourses"
          placeholder="Enrolled Courses"
          value={newStudent.enrolledCourses}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="completedCourses"
          placeholder="Completed Courses"
          value={newStudent.completedCourses}
          onChange={handleInputChange}
        />
        <button type="button" onClick={handleAddStudent}>Add Student</button>
      </form>
    </div>
  );
};

export default StudentList;
