// src/api.js

import axios from 'axios';

// Set up the base URL for the API
const API_URL = 'http://localhost:5000'; // Replace with your actual backend API URL

// Function to get courses
export const getCourses = () => {
  return axios.get(`${API_URL}/courses`);
};

// Function to get students
export const getStudents = () => {
  return axios.get(`${API_URL}/students`);
};

// Function to add a course
export const addCourse = (course) => {
  return axios.post(`${API_URL}/courses`, course);
};

// Function to add a student
export const addStudent = (student) => {
  return axios.post(`${API_URL}/students`, student);
};

// Function to enroll a student in a course
export const enrollStudentInCourse = (studentId, courseId) => {
  return axios.post(`${API_URL}/enroll`, { studentId, courseId });
};

// Function to delete a course
export const deleteCourse = (courseId) => {
  return axios.delete(`${API_URL}/courses/${courseId}`);
};

// Function to delete a student
export const deleteStudent = (studentId) => {
  return axios.delete(`${API_URL}/students/${studentId}`);
};
