import React, { useState } from 'react';
import { addStudent } from '../api';

const AddStudent = () => {
  const [name, setName] = useState('');
  const [department, setDepartment] = useState('');
  const [semester, setSemester] = useState('');
  const [enrolledCourses, setEnrolledCourses] = useState('');
  const [completedCourses, setCompletedCourses] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const student = {
      name,
      department,
      semester: parseInt(semester),
      enrolledCourses: enrolledCourses.split(',').map(course => course.trim()),
      completedCourses: completedCourses.split(',').map(course => course.trim())
    };

    const newStudent = await addStudent(student);
    console.log('New student added:', newStudent);
    setName('');
    setDepartment('');
    setSemester('');
    setEnrolledCourses('');
    setCompletedCourses('');
  };

  return (
    <div>
      <h2>Add New Student</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Department"
          value={department}
          onChange={(e) => setDepartment(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Semester"
          value={semester}
          onChange={(e) => setSemester(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Enrolled Courses (comma-separated)"
          value={enrolledCourses}
          onChange={(e) => setEnrolledCourses(e.target.value)}
        />
        <input
          type="text"
          placeholder="Completed Courses (comma-separated)"
          value={completedCourses}
          onChange={(e) => setCompletedCourses(e.target.value)}
        />
        <button type="submit">Add Student</button>
      </form>
    </div>
  );
};

export default AddStudent;
