// src/components/StudentList.js

import React, { useState, useEffect } from 'react';
import { getStudents, deleteStudent } from '../api'; // Import the deleteStudent function

const StudentList = () => {
  const [students, setStudents] = useState([]);

  useEffect(() => {
    // Fetch students from the backend when the component mounts
    getStudents()
      .then((response) => {
        console.log('Students data:', response.data); // Log the data to verify it's coming in
        if (Array.isArray(response.data)) {
          setStudents(response.data); // Update the state with the fetched students
        } else {
          console.error('Students data is not an array:', response.data);
        }
      })
      .catch((error) => {
        console.error('Error fetching students:', error);
      });
  }, []);

  // Handle the deletion of a student
  const handleDelete = (studentId) => {
    deleteStudent(studentId)
      .then(() => {
        setStudents(students.filter(student => student.id !== studentId)); // Remove the deleted student from the state
      })
      .catch((error) => {
        console.error('Error deleting student:', error);
      });
  };

  return (
    <div>
      <h2>Students</h2>
      {students.length === 0 ? (
        <p>No students available.</p>
      ) : (
        students.map((student) => (
          <div key={student.id}>
            <p>{student.name}</p>
            <button onClick={() => handleDelete(student.id)}>Delete</button>
          </div>
        ))
      )}
    </div>
  );
};

export default StudentList;
